class BackendService : Service() {
    private val _storagePath = "/"
//    @SuppressLint("StaticFieldLeak")
//    private val _playerDelegate = EaseMusicPlayerDelegate()
    private val _channelId: String = "EaseMusicBackendServiceChannel"

    override fun onCreate() {
//        _playerDelegate.onCreate(this)
//        apiBuildBackend(
//            AsyncRuntimeAdapter(),
//            _playerDelegate
//        )
//        apiStartBackend(ArgInitializeApp(
//            appDocumentDir = normalizePath(filesDir.absolutePath),
//            appCacheDir = normalizePath(cacheDir.absolutePath),
//            storagePath = _storagePath
//        ))
        createNotificationChannel()
    }

    override fun onStartCommand(intent: Intent?, flags: Int, startId: Int): Int {
        val notification = NotificationCompat.Builder(this, _channelId)
            .setContentTitle("Ease Music Player Backend")
            .setContentText("Ease Music Player Backend Service is running")
            .setOngoing(true)
            .build();

        startForeground(1, notification);

        LocalBroadcastManager.getInstance(this).sendBroadcast(Intent(BACKEND_STARTED_ACTION))
        return START_NOT_STICKY
    }

    override fun onDestroy() {
//        apiDestroyBackend()
//        _playerDelegate.onDestroy()
    }

    override fun onBind(p0: Intent?): IBinder? {
        return null
    }

    override fun onTaskRemoved(rootIntent: Intent?) {
        stopSelf()
    }

    private fun createNotificationChannel() {
        val serviceChannel = NotificationChannel(
            _channelId,
            "Foreground Service Channel",
            NotificationManager.IMPORTANCE_LOW
        )

        val manager = getSystemService(
            NotificationManager::class.java
        )
        manager?.createNotificationChannel(serviceChannel)
    }
}
